  theme_minimal(base_size = 12) +
  theme(
    plot.title = element_text(face = "bold", size = 14, color = "#00247D"),
    plot.subtitle = element_text(size = 10, color = "#555555"),
    plot.caption = element_text(size = 8, color = "#999999"),
